mapper.select:
SELECT	post_no
		, title
		, reg_id
		, reg_date
		, matching_completed
FROM	MATCH_POST
WHERE	1=1

	AND post_no = #{post_no}


	AND title = #{title}


	AND reg_id = #{reg_id}


	AND matching_completed = #{matching_completed}

mapper.insert:
INSERT INTO MATCH_POST
	(
		reg_id
		, title
		, description
		, match_date
		, match_court_code
		, match_type
		, matching_completed
		, reg_date
	)
VALUES
	(
		#{reg_id}
		, #{title}
		, #{description}
		, #{match_date}
		, #{match_court_code}
		, #{match_type}
		, #{matching_completed}
		, NOW()
	)

mapper.update:
UPDATE 	MATCH_POST
SET		title = #{title}
		, description = #{description}
		, match_date = #{match_date}
		, match_court_code = #{match_court_code}
		, match_type = #{match_type}
WHERE	post_no = #{post_no}
