mapper.insert:
INSERT INTO EVAL_USER
	(
		src_user
		, des_user
		, m_score
		, description
	)
VALUES
	(
		#{src_user}
		, #{des_user}
		, #{m_score}
		, #{description}
	)

mapper.update:
UPDATE	USER
SET		password = #{password}
		, name = #{name}
		, birth = #{birth}
		, phone = #{phone}
		, email = #{email}
		, club_code = #{club_code}
		, ntrp = #{ntrp}
WHERE	id = #{id}
